function checkBishLetters(id1, id2) {
    let letters = "ABCDEFGH", start = 0, finish = 0, i, j, k, p;
    let letter1 = id1[0], letter2 = id2[0];

    for (i = 0; i < 8; i++) {
        if (letters[i] == letter1)
            start = i;
        else if (letters[i] == letter2)
            finish = i;
    }
    if ((start > finish && parseInt(id1[1]) > parseInt(id2[1])) || (start < finish && parseInt(id1[1]) < parseInt(id2[1]))) {
        if (start > finish)
            i = finish + 1, j = start, k = parseInt(id2[1]) + 1;
        else
            i = start + 1, j = finish, k = parseInt(id1[1]) + 1;
        p = 1;
    }
    else { // if ((start > finish && parseInt(id1[1]) < parseInt(id2[1])) || (start < finish && parseInt(id1[1]) > parseInt(id2[1])))
        if (start > finish)
            i = finish + 1, j = start, k = parseInt(id2[1]) - 1;
        else
            i = start + 1, j = finish, k = parseInt(id1[1]) - 1;
        p = -1;
    }

    for (; i < j; i++, k += p) {
        if (document.getElementById(letters[i] + k).innerHTML !== "")
            return false;
    }
    return Math.abs(finish - start) === Math.abs(parseInt(id1[1]) - parseInt(id2[1]));
}

function checkRookLetters(id1, id2) {
    let letters = "ABCDEFGH";
    let start, finish, i;
    let cell;

    if (id1[0] === id2[0]) {
        if (parseInt(id1[1]) > parseInt(id2[1])) start = parseInt(id2[1]), finish = parseInt(id1[1]);
        else start = parseInt(id1[1]), finish = parseInt(id2[1]);

        for (i = start + 1; i < finish; i++) {
            cell = document.getElementById(id1[0] + i);
            if (cell.innerHTML !== "")
                return false;
        }
    }
    else if (id1[1] == id2[1]) {
        for (i = 0; i < 8; i++) {
            if (id1[0] == letters[i])
                start = i;
            else if (id2[0] == letters[i])
                finish = i;
        }
        if (start > finish) {
            let tmp = start;
            start = finish;
            finish = tmp;
        }
        for (i = start + 1; i < finish; i++) {
            cell = document.getElementById(letters[i] + id1[1]);
            if (cell.innerText !== "" || cell.innerHTML !== "")
                return false;
        }
    }
    else return false;
    return true;
}

function checkHorseLetters(id1, id2) {
    let letters = "ABCDEFGH", startLet, finishLet;
    let startNum = parseInt(id1[1]), finishNum = parseInt(id2[1]);
    
    for (let i = 0; i < 8; i++) {
        if (letters[i] === id1[0]) startLet = i;
        else if (letters[i] === id2[0]) finishLet = i;
    }

    if ((Math.abs(finishNum - startNum) === 2 && Math.abs(finishLet - startLet) === 1) ||
        (Math.abs(finishNum - startNum) === 1 && Math.abs(finishLet - startLet) === 2))
        return true;
    return false;
}

