export function makeMove(start, finish) {
    if (start == null || finish == null)
        if (white_flag == true)
            return -1;
    if (start.id === finish.id) return -1;
    if ((white_flag &&
            (finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`)) ||
        (!white_flag &&
            (finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">` ||
            finish.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`)))
        return -1;
    /*** PAWN MOVE ***/
    if ((!white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`) ||
        (white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`)) {
        if (checkPawnLetters(start.id, finish.id)) {
                finish.innerText = start.innerText;
                finish.innerHTML = start.innerHTML;
                start.innerText = "";
                start.innerHTML = "";
        }
        else return 0;
    }
    /*** BISHOP MOVE ***/
    else if ((!white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`) ||
             (white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`)) {
        if (checkBishLetters(start.id, finish.id)) {
            finish.innerText = start.innerText;
            finish.innerHTML = start.innerHTML;
            start.innerText = "";
            start.innerHTML = "";
        }
        else return 0;
    }
    /*** ROOK MOVE ***/
    else if ((!white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`) ||
             (white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`)) {
        if (checkRookLetters(start.id, finish.id)) {
            finish.innerText = start.innerText;
            finish.innerHTML = start.innerHTML;
            start.innerText = "";
            start.innerHTML = "";
        }
        else return 0;
    }
    /*** QUEEN MOVE ***/
    else if ((!white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`) ||
             (white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`)) {
        if (checkRookLetters(start.id, finish.id) || checkBishLetters(start.id, finish.id)) {
                finish.innerText = start.innerText;
                finish.innerHTML = start.innerHTML;
                start.innerText = "";
                start.innerHTML = "";
            }
        else return 0;
    }
    /*** HORSE MOVE ***/
    else if ((!white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`) ||
             (white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`)) {
        if (checkHorseLetters(start.id, finish.id)) {
                finish.innerText = start.innerText;
                finish.innerHTML = start.innerHTML;
                start.innerText = "";
                start.innerHTML = "";
            }
        else return 0;
    }
    /*** KING MOVE ***/
    else if ((!white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`) ||
             (white_flag && start.innerHTML == `<img src="data:image/svg+xml;base64," alt="" class="img">`)) {
        if (checkKingLetters(start.id, finish.id)) {
                finish.innerText = start.innerText;
                finish.innerHTML = start.innerHTML;
                start.innerText = "";
                start.innerHTML = "";
            }
        else return 0;
    }
    else return -1;
    current_move++;
    white_flag = !white_flag;
    return 1;
}